const menuBtn=document.getElementById("menuMobile"),mainMenu=document.getElementById("mainMenu"),closeMenu=document.getElementById("closeMenu");function dropDown(e){document.getElementById(e).classList.toggle("opn-ddw")}function donutChart(){const e=document.getElementById("courseProgress"),n=e.getAttribute("data-course-progress");document.getElementById("donutSegment").setAttribute("stroke-dasharray",`${n} ${100-n}`),e.innerText=`${n}%`}menuBtn.addEventListener("click",(()=>{mainMenu.classList.add("opn-menu")})),closeMenu.addEventListener("click",(()=>{mainMenu.classList.remove("opn-menu")}));