const menuBtn=document.getElementById("menuMobile"),mainMenu=document.getElementById("mainMenu"),closeMenu=document.getElementById("closeMenu");function dropDown(e){document.getElementById(e).classList.toggle("opn-ddw")}menuBtn.addEventListener("click",(()=>{mainMenu.classList.add("opn-menu")})),closeMenu.addEventListener("click",(()=>{mainMenu.classList.remove("opn-menu")}));const courseProgress=document.getElementById("courseProgress"),donutPercentage=courseProgress.getAttribute("data-course-progress"),donutSegment=document.getElementById("donutSegment");function donutChart(){donutSegment.setAttribute("stroke-dasharray",`${donutPercentage} ${100-donutPercentage}`),courseProgress.innerText=`${donutPercentage}%`}document.addEventListener("DOMContentLoaded",(function(e){donutChart()}));